import { Component, Renderer2, AfterViewInit } from '@angular/core';

@Component({
  selector: 'app-root',
  template: './app.component.html',
  styleUrls: ['./app.component.css'],
})
export class AppComponent implements AfterViewInit {
  title_name = 'Curriculum Vitae';
  title_fontsize = 10;
  title_bottom = 80;
  inSquare = false;
  titleon = false;
  inIcon = false;

  constructor(private renderer: Renderer2) {}

  ngAfterViewInit() {
    const title = this.renderer.createElement('div');
    this.renderer.setProperty(title, 'innerHTML', this.title_name);
    this.renderer.addClass(title, 'title');
    this.renderer.appendChild(document.body, title);

    const icon = this.renderer.createElement('div');
    this.renderer.addClass(icon, 'icon');
    this.renderer.setProperty(icon, 'innerHTML', '&#9776;');
    this.renderer.appendChild(document.body, icon);

    const rectangle = this.renderer.createElement('div');
    this.renderer.addClass(rectangle, 'rectangle');
    this.renderer.appendChild(document.body, rectangle);

    this.renderer.listen(window, 'load', () => this.sizer(icon));
    this.renderer.listen(window, 'resize', () => this.sizer(icon));

    this.renderer.listen(icon, 'click', () => {
      if (!this.inSquare) {
        this.renderer.setStyle(rectangle, 'display', 'block');
        this.inSquare = true;
        this.renderer.setStyle(icon, 'transform', 'rotate(90deg)');
      } else {
        this.renderer.setStyle(rectangle, 'display', 'none');
        this.inSquare = false;
        this.renderer.setStyle(icon, 'transform', 'rotate(0deg)');
      }
    });

    this.renderer.listen(title, 'mouseover', () => {
      this.renderer.setStyle(title, 'color', 'grey');
      this.renderer.setStyle(icon, 'opacity', '1');
      this.titleon = true;
    });

    this.renderer.listen(icon, 'mouseover', () => {
      this.inIcon = true;
    });

    this.renderer.listen(icon, 'mouseout', () => {
      this.inIcon = false;
      setTimeout(() => {
        if (!this.inSquare && !this.titleon && !this.inIcon) {
          this.renderer.setStyle(icon, 'opacity', '0');
        }
      }, 2000);
    });

    this.renderer.listen(title, 'mouseout', () => {
      this.renderer.setStyle(title, 'color', 'white');
      this.titleon = false;
      setTimeout(() => {
        if (!this.inSquare && !this.titleon && !this.inIcon) {
          this.renderer.setStyle(icon, 'opacity', '0');
        }
      }, 2000);
    });
  }

  sizer(icon: any) {
    this.renderer.setStyle(icon, 'left', `25vw`);
    this.renderer.setStyle(icon, 'bottom', `${this.title_bottom + 1}vh`);
    // Set rectangle position relative to title
    // this.renderer.setStyle(rectangle, 'left', `30vw`);
    // this.renderer.setStyle(rectangle, 'bottom', `${this.title_bottom - this.title_fontsize - 90}vh`);
  }
}
